import React, { useEffect, useState } from "react";
import classes from "./categories.module.css";

const Categories = () => {
  const URL_CATEGORIES =
    "https://www.themealdb.com/api/json/v1/1/categories.php";

  const URL_RECIPES =
    "https://www.themealdb.com/api/json/v1/1/filter.php?c=Seafood";

  const [categories, setCategories] = useState([]);
  const [activecategory, setActiveCategory] = useState("");
  const [recipes, setRecipes] = useState([]);

  useEffect(() => {
    const fetchCategories = async () => {
      try {
        const res = await fetch(URL_CATEGORIES);
        const data = await res.json();
        setCategories(data.categories.slice(0, 9));
        setActiveCategory(data.categories[0].strCategory);
      } catch (error) {
        console.log(error);
      }
    };
    fetchCategories();
  }, []);

  useEffect(() => {
    const fetchRecipes = async () => {
      try {
        const res = await fetch(`${URL_RECIPES}${activecategory}`);
        const data = await res.json();
        setRecipes(data.recipes.slice(0, 9));
      } catch (error) {
        console.log(error);
      }
    };
    activecategory && fetchRecipes();
  }, []);

  return <div>const res = await fetch(URL_RECIPES)</div>;
};

export default Categories;
