import React, { useEffect, useState } from "react";
import classes from "./favouriteFoods.module.css";
import { AiOutlineArrowLeft, AiOutlineArrowRight } from "react-icons/ai";

const FavouriteFoods = () => {
  const URL = "https://www.themealdb.com/api/json/v1/1/filter.php?c=Seafood";
  const [recipes, setRecipes] = useState([]);
  const [showRecipes, setShowRecipes] = useState(0);

  useEffect(() => {
    const fetchRecipes = async () => {
      try {
        const res = await fetch(URL);
        const data = res.json();
        setRecipes(data.meals.slice(0, 8));
      } catch (error) {
        console.log(error);
      }
    };
    fetchRecipes();
  }, []);

  const handleArrow = (direction) => {
    if (direction === "left") {
      if (showRecipes === 0) {
        setShowRecipes(recipes.length - 1);
      } else {
        setShowRecipes((prev) => prev - 1);
      }
    }

    if (direction == "right") {
      if (showRecipes === recipes.length - 1) {
        setShowRecipes(0);
      } else {
        setShowRecipes((prev) => prev + 1);
      }
    }
  };

  return (
    <div className={classes.container}>
      <div className={classes.wrapper}>
        <div className={classes.titles}>
          <h5>Recipes people like the most</h5>
          <h2>Our clients' favourite recipes</h2>
        </div>
        <div className={classes.recipes}>
          <AiOutlineArrowLeft
            onClick={() => handleArrow("left")}
            className={classes.leftArrow}
          />
          {recipes?.map((recipe) => (
            <div
              style={{ transform: `translate(-$(showRecipe * 750)px)` }}
              className={classes.recipe}
              key={recipe.idMeal}
            >
              <img src={recipe.strMealThumb} alt="" />
              <h3>{recipe?.strMeal}</h3>
            </div>
          ))}
          <AiOutlineArrowRight
            onClick={() => handleArrow("left")}
            className={classes.rightArrow}
          />
        </div>
        <div className={classes.dots}>
          {recipes.map((_, index) => (
            <div
              className={`${classes.dot} ${
                showRecipes === index && classes.activeDot
              }`}
              key={index}
              onClick={() => setShowRecipes(index)}
            />
          ))}
        </div>
      </div>
    </div>
  );
};

export default FavouriteFoods;
